mapper.insert:
INSERT INTO board (
			category
			, title
			, writer
			, description
			, file_count)
VALUES (#{category}
		, #{title}
		, #{writer}
		, #{description}
		, #{fileCount})

mapper.update:
UPDATE board 
SET category = #{category}
	, title = #{title}
	, description = #{description}
	, file_count = #{fileCount}
	, mod_at = now()

UPDATE board 
SET read_count = read_count + #{i}
WHERE bno = #{bno}

UPDATE board 
SET cmt_qty = cmt_qty + #{i}
WHERE bno = #{bno}
